  \draw[vector, seq1] (0,0,0) --
    node [midway, above] {$v$} \v;
  \draw[thin, densely dotted] \v -- \projxy\v;
  \draw[vector, seq2] (0,0,0) --
    node [midway, below] {\strut$w$} \w;
  \draw[thin, densely dotted] \w -- \projxy\w;

  \point at (0,0,0);
\end{scope}

